const form=document.querySelector("#potm-form");function showToast(t){const e=document.createElement("div");e.classList.add("toast",t.success?"success":"failed");const o=document.createElement("p");o.textContent=t.message,e.appendChild(o),document.body.appendChild(e),setTimeout(()=>{e.classList.add("disappear")},3e3)}form.addEventListener("submit",t=>{t.preventDefault();const e=new FormData(form);fetch(t.target.getAttribute("action"),{method:t.target.getAttribute("method"),body:e}).then(t=>t.json()).then(t=>showToast(t))});